import React from "react";
import styled from "styled-components";
import DescriptionContentBlockDetail from "./DescriptionContentBlockDetail";

const Container = styled.div`
  width: 100%;
  background-color: #eef3c0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 48px 124.5px;
`;

const AvatarContainer = styled.div`
  width: 462.5px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const AvatarContainerColumn = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  .left_text {
    color: white;
  }
`;

const AvatarImgContainer = styled.div`
  width: 145px;
  height: 145px;
  background-color: white;
`;

const AvatarImg = styled.img``;

const AvatarDescription = styled.span`
  font-family: CoreGothicE-8;
  font-size: 16px;
  font-weight: 900;
  color: #000000;
`;

const AvatarInfo = styled.span`
  font-family: CoreGothicE-5;
  font-size: 11px;
  font-weight: 500;
  color: #000000;
`;

const DescriptionContentBlock_1 = () => (
  <Container>
    <AvatarContainer>
      <AvatarContainerColumn>
        <AvatarImgContainer>
          <AvatarImg></AvatarImg>
        </AvatarImgContainer>
      </AvatarContainerColumn>
      <AvatarContainerColumn>
        <AvatarDescription className="left_text">
          이제 인터넷 강의는 지긋지긋해요 <br />
          돈은 돈대로 쓰고 남는 건 없어요
        </AvatarDescription>
        <AvatarInfo>대학생 김모씨 (23세)</AvatarInfo>
      </AvatarContainerColumn>
    </AvatarContainer>
    <AvatarContainer>
      <AvatarContainerColumn class="right_text">
        <AvatarDescription>
          이제 인터넷 강의는 지긋지긋해요 <br />
          돈은 돈대로 쓰고 남는 건 없어요
        </AvatarDescription>
        <AvatarInfo>대학생 김모씨 (23세)</AvatarInfo>
      </AvatarContainerColumn>
      <AvatarContainerColumn>
        <AvatarImgContainer>
          <AvatarImg></AvatarImg>
        </AvatarImgContainer>
      </AvatarContainerColumn>
    </AvatarContainer>
    <DescriptionContentBlockDetail></DescriptionContentBlockDetail>
  </Container>
);
export default DescriptionContentBlock_1;
