import React from 'react';
import styled from 'styled-components';

const CardNewsContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: space-around;
  align-items: center;
`;

const CardNewsBlockDefault = styled.div`
  width: 195px;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  overflow: hidden;
`;

const CardNewsImg = styled.img`
  width: 80px;
  height: 80px;
  transition: all 0.2s linear;
`;

const BlockCardNewsInfo = styled.div`
  width: 195px;
  left: 0;
  bottom: 0;
  position: absolute;
  background-color: #273949;
  display: flex;
  align-items: center;
  justify-content: space-between;
  transform: translate(0, 100%);
  transition: all 0.2s linear;
`;

const BlockCardNews = styled.div`
  &:hover ${CardNewsImg} {
    transform: translateY(-25px);
  }
  &:hover ${BlockCardNewsInfo} {
    transform: translate(0, 0);
  }
`;

const CardNewsColumn = styled.div`
  height: 50px;
  width: 100%;
  padding: 12px 10px 10px 10px;
`;

const RewviewInfo = styled.div`
  font-size: 10px;
  font-weight: 700;
  color: white;
  margin-bottom: 2px;
`;
const InfoButton = styled.button``;

const CardNewsBlock = () => (
  <Container>
    <CardNewsDescription></CardNewsDescription>
    <CardNewsDescription></CardNewsDescription>
    <CardNewsTitle></CardNewsTitle>
    <CardNewsContainer>
      <CardNewsBlockDefault>
        <BlockCardNews>
          <CardNewsImg
            src={require('../../Assets/Block_Images/1.png')}
          ></CardNewsImg>
          <BlockCardNewsInfo>
            <CardNewsColumn>
              <RewviewInfo>앱 출시!?</RewviewInfo>
            </CardNewsColumn>
            <CardNewsColumn>
              <InfoButton>살펴보기</InfoButton>
            </CardNewsColumn>
          </BlockCardNewsInfo>
        </BlockCardNews>
      </CardNewsBlockDefault>
      <CardNewsBlockDefault>
        <BlockCardNews>
          <CardNewsImg
            src={require('../../Assets/Block_Images/1.png')}
          ></CardNewsImg>
          <BlockCardNewsInfo>
            <CardNewsColumn>
              <RewviewInfo>앱 출시!?</RewviewInfo>
            </CardNewsColumn>
            <CardNewsColumn>
              <InfoButton>살펴보기</InfoButton>
            </CardNewsColumn>
          </BlockCardNewsInfo>
        </BlockCardNews>
      </CardNewsBlockDefault>
      <CardNewsBlockDefault>
        <BlockCardNews>
          <CardNewsImg
            src={require('../../Assets/Block_Images/1.png')}
          ></CardNewsImg>
          <BlockCardNewsInfo>
            <CardNewsColumn>
              <RewviewInfo>앱 출시!?</RewviewInfo>
            </CardNewsColumn>
            <CardNewsColumn>
              <InfoButton>살펴보기</InfoButton>
            </CardNewsColumn>
          </BlockCardNewsInfo>
        </BlockCardNews>
      </CardNewsBlockDefault>
      <CardNewsBlockDefault>
        <BlockCardNews>
          <CardNewsImg
            src={require('../../Assets/Block_Images/1.png')}
          ></CardNewsImg>
          <BlockCardNewsInfo>
            <CardNewsColumn>
              <RewviewInfo>앱 출시!?</RewviewInfo>
            </CardNewsColumn>
            <CardNewsColumn>
              <InfoButton>살펴보기</InfoButton>
            </CardNewsColumn>
          </BlockCardNewsInfo>
        </BlockCardNews>
      </CardNewsBlockDefault>
    </CardNewsContainer>
  </Container>
);

export default CardNewsBlock;
