import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  width: 100%;
  height: 100%;
  background-color: #d3eeff;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding-top: 100px;
`;

const CourseHeader = styled.div`
  width: 300px;
  height: 50px;
  color: black;
  font-size: 15pt;
  border: 1px solid black;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 100px;
`;

const MainTitle = styled.div`
  font-size: 48px;
  margin-top: 48px;
  text-align: center;
  color: black;
`;

const LecturerBlock = () => (
  <Container>
    <CourseHeader>코더스하이의 강점</CourseHeader>
    <MainTitle>
      애플이 선정하고
      <br />
      유명 대학들이 선택한
      <br /> APP 개발 코스
    </MainTitle>
  </Container>
);

export default LecturerBlock;
