import React from 'react';
import styled from 'styled-components';

const CardNewsBlockDefault = styled.div`
  width: 195px;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  overflow: hidden;
`;

const CardNewsImg = styled.img`
  width: 80px;
  height: 80px;
  transition: all 0.2s linear;
`;

const BlockCardNewsInfo = styled.div`
  width: 195px;
  left: 0;
  bottom: 0;
  position: absolute;
  background-color: #273949;
  display: flex;
  align-items: center;
  justify-content: space-between;
  transform: translate(0, 100%);
  transition: all 0.2s linear;
`;

const BlockCardNews = styled.div`
  &: hover ${CardNewsImg} {
    transform: translateY(-25px);
  }
  &: hover ${BlockCardNewsInfo} {
    transform: translate(0, 0);
  }
`;

const CardNewsColumn = styled.div`
  height: 50px;
  width: 100%;
  padding: 12px 10px 10px 10px;
`;

const RewviewInfo = styled.div`
  font-size: 10px;
  font-weight: 700;
  color: white;
  margin-bottom: 2px;
`;
const Info_Button = styled.button``;

const CardNewsBlock = () => (
  <ReviewBlockDefault>
    <BlockReview>
      <ReviewImg src={require('../../Assets/Block_Images/1.png')}></ReviewImg>
      <BlockReviewInfo>
        <ReviewColumn>
          <RewviewInfo>앱 출시!?</RewviewInfo>
        </ReviewColumn>
        <ReviewColumn>
          <Info_Button>살펴보기</Info_Button>
        </ReviewColumn>
      </BlockReviewInfo>
    </BlockReview>
  </ReviewBlockDefault>
);

export default CardNewsBlock;
