import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding-top: 100px;
`;

const CourseHeader = styled.div`
  width: 300px;
  height: 50px;
  color: black;
  font-size: 15pt;
  border: 1px solid black;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 100px;
`;

const CouserProcessContainer = styled.div`
  width: 60%;
  height: 100%;
  display: flex;
  align-items: center;
  margin-top: 100px;
  .left_text {
    width: 200%;
    text-align: left;
  }
  .right_text {
    width: 200%;
    text-align: right;
  }
  .right_img {
    align-items: flex-end;
  }
  .left_img {
    align-items: flex-start;
  }
`;

const CouserProcessContainerColumn = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;

const AvatarImg = styled.img`
  width: 145px;
  height: 145px;
  object-fit: contain;
`;

const AvatarDescription = styled.span`
  font-family: CoreGothicE-8;
  font-size: 16px;
  font-weight: 900;
  color: #000000;
  padding-left: 23px;
  padding-right: 23px;
`;

const AvatarInfo = styled.span`
  font-family: CoreGothicE-5;
  font-size: 11px;
  font-weight: 500;
  color: #000000;
  margin-top: 10px;
  padding-left: 23px;
  padding-right: 23px;
`;

const DividerW = () => (
  <Container>
    <CourseHeader>실시간 원격 코스 진행 방식</CourseHeader>
    <CouserProcessContainer>
      <CouserProcessContainerColumn className="right_img">
        <AvatarImg
          src={require('../../Assets/Images/youngman/group29.png')}
        ></AvatarImg>
      </CouserProcessContainerColumn>
      <CouserProcessContainerColumn className="left_text">
        <AvatarDescription>
          이제 인터넷 강의는 지긋지긋해요 <br />
          돈은 돈대로 쓰고 남는 건 없어요
        </AvatarDescription>
        <AvatarInfo>대학생 김모씨 (23세)</AvatarInfo>
      </CouserProcessContainerColumn>
    </CouserProcessContainer>
  </Container>
);

export default DividerW;
