import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";


const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #007bec;
`;

const Cover = styled.img`
  width: 100%;
  height: 100%;
  src: url(${(props) => props.bgImage});
`;

const DefaultButton = styled.button`
  width: 25em;
  height: 2.5em;
  font-size: 2.5em;
  font-family: 'CoreGothicE-3';
  font-weight: 800;
  color: #007bec;
  border-radius: 1em;
`
const CollapseButtonContainer = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
`

const CollapseButtonColumn = styled.div`
  width: 100%;
  height: 100%;
`

const CollapseButton = styled.button`
  width: 100%;
  height: 100%;
  background:none;
  border:0px; 
  font-size: 20px;
  color: black;
`


const HomePresenter = ({max_images, collapse}) => (
  <Container>
    {
      max_images.map( (image, index) => 
         { if (image === "1.png") {
           const Block = 
           <>
              <Cover key={index} src={require(`../../Assets/Block_Images/${image}`)} />
              <DefaultButton>등록하러 가기</DefaultButton>
           </>
           return Block
         } else if (image === "4.png") {
           const Block = 
            <div className="block">
            <div className="block__default block__game-center">
              <img src="src/images/game.png" alt="" className="game-center__icon" />
              <div className="block__game-center-info">
                <div className="game-center-info__column">
                  <div className="info__title">Game Center</div>
                  <div className="info__creator">by Jacob Cummings</div>
                </div>
                <div className="game-center-info__column">
                  <button>Take a look</button>
                </div>
              </div>
            </div>
          </div>
           return Block
        } else if (image === "5.png") {
           const Block =
            <>
              <Cover key={index} src={require(`../../Assets/Block_Images/${image}`)} />
              <CollapseButtonContainer>
                <CollapseButtonColumn>
                <CollapseButton onClick={collapse}>중앙대학교 사례 보기</CollapseButton>
                </CollapseButtonColumn>
                <CollapseButtonColumn>
                <CollapseButton onClick={collapse}>한양대학교 사례 보기</CollapseButton>
                </CollapseButtonColumn>
              </CollapseButtonContainer>
            </>
           return Block
         } else {
          return <Cover key={index} src={require(`../../Assets/Block_Images/${image}`)} />
         }}

      )
    }
  </Container>
);

HomePresenter.propTypes = {
  max_images: PropTypes.array,
  collapse: PropTypes.func.isRequired
};


export default HomePresenter;
