import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  width: 100%;
  height: 100%;
  background-color: #d3eeff;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding-top: 100px;
`;

const CourseHeader = styled.div`
  width: 300px;
  height: 50px;
  color: black;
  font-size: 15pt;
  border: 1px solid black;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 100px;
`;

const MainTitle = styled.div`
  font-size: 48px;
  margin-top: 48px;
  text-align: center;
  color: black;
`;

const Description = styled.div`
  margin-top: 48px;
  font-size: 20px;
  color: black;
  text-align: center;
`;

const HalfBackground = styled.span`
  background: linear-gradient(to top, yellow 50%, transparent 50%);
`;

const LecturerBlock = () => (
  <Container>
    <CourseHeader>코더스하이의 강점</CourseHeader>
    <MainTitle>
      애플이 선정하고
      <br />
      유명 대학들이 선택한
      <br /> APP 개발 코스
    </MainTitle>
    <Description>
      코더스하이의 앱 개발 코스는
      <br /> 애플이 선정한 교수진이 개발하고
      <br /> 서울시 내 10여개 대학들이 경쟁력 강화를 위해
      <br /> 선택한 앱 개발 특강입니다
    </Description>
  </Container>
);

export default LecturerBlock;
